    //取出cookie token值 , 並取得產品列表
    const token = document.cookie
        .split("; ")
        .find((row) => row.startsWith("shopToken="))
        ?.split("=")[1];
    console.log(token);
    axios.defaults.headers.common['Authorization'] = token; //4.登入時將token帶入


